import React, { Component } from 'react'
import TopNav from './navbar'
import Map from './mapbox'

export default class Main extends React.Component {

  state = {
    listings: []
  }

  constructor(props) {
    super(props)
    this.mapBoxElement = React.createRef()
  }

  componentDidMount() {
    fetch('http://localhost:4000/api/v1/listings')
    .then(r => r.json())
    .then(parsed => {
      this.setState({listings: parsed})
      console.log(this.state.listings)
    })
  }

  updateListings = (item) => {
    let listings = this.state.listings
    this.setState({listings: [...listings, item]})
  }

  clickHandler = () => {
    this.mapBoxElement.current.updateMapBoxState()
  }

  render () {
    return(
    <div onClick={() => this.clickHandler()}>
      <TopNav updateListings={this.updateListings}/>
      <Map listings={this.state.listings}/>
    </div>
    )
  }
}
