import React, { Component } from 'react'
import ReactMapboxGl, { Marker, Layer, Feature, Popup } from "react-mapbox-gl";
import { GeoJSONLayer } from "react-mapbox-gl";

const Map = ReactMapboxGl({
  accessToken: process.env.REACT_APP_KEY
})

const mbxGeocoding = require('@mapbox/mapbox-sdk/services/geocoding');
const geocodingClient = mbxGeocoding({ accessToken: process.env.REACT_APP_KEY });

class Mapbox extends React.Component {

state = {
  listings: [],
  currentListing: null
}

componentDidMount() {
  fetch('http://localhost:4000/api/v1/listings')
  .then(r => r.json())
  .then(parsed => {
    this.setState({listings: parsed})
    console.log(this.state.listings)

    this.state.listings.map(listing => {
        let locations = this.state.locations
        let coordinates = listing.coordinates.split(",").map(n => parseFloat(n))
        this.setState({locations: [...locations, coordinates]})
    })
    console.log("locations", this.state.locations)
  })
}

makeFeatures = () => {
  let pins = this.state.listings.map(listing =>
    <Marker coordinates={listing.coordinates.split(",").map(n => parseFloat(n))} onClick={(e, listing) => this.clickHandler(e, listing)}/>
  )
  console.log("pins",pins)
  return(pins)
}

makePopups = () => {
  let popups = this.state.listings.map(listing =>
  <Popup
    coordinates={listing.coordinates.split(",").map(n => parseFloat(n))}
    anchor="top"
  >
      <ul>
        <li>Item: {listing.item}</li>
        <li>Condition: {listing.condition}</li>
        <li>Location: {listing.location}</li>
      </ul>
  </Popup>
)
  return( popups )
}

clickHandler = (e) => {
  console.log(e.lngLat)
  let coordinates = []
  coordinates.push(e.lngLat.lng)
  coordinates.push(e.lngLat.lat)
  console.log(coordinates)
  return (
    <Popup coordinates={coordinates}
    >
      <div>
        hello
      </div>
    </Popup>
  )
}

render() {
  return (
    <div>
      <Map
      style="mapbox://styles/mapbox/streets-v9"
      center={[-74.0060, 40.7128]}
      containerStyle={{
        height: "100vh",
        width: "100vw"
      }}>

        <Layer
        type="symbol"
        id="marker"
        layout={{ "icon-image": "rocket-15" }}
        color="#01FEDC">

          {this.makeFeatures()}

        </Layer>
        {this.makePopups()}



      </Map>


    </div>
  )
}


}

export default Mapbox
