import React, { Component } from 'react'
import ReactMapboxGl, { Layer, Feature } from "react-mapbox-gl";
import { GeoJSONLayer } from "react-mapbox-gl";

const Map = ReactMapboxGl({
  accessToken: "pk.eyJ1IjoianRhbmc5MyIsImEiOiJjanB1OXIyZXQwZWZ4M3hudHM4NGl0cmc3In0.q6DGhvJla097_6b-RMi0Fw"
})

const mbxGeocoding = require('@mapbox/mapbox-sdk/services/geocoding');
const geocodingClient = mbxGeocoding({ accessToken: 'pk.eyJ1IjoianRhbmc5MyIsImEiOiJjanB1OXIyZXQwZWZ4M3hudHM4NGl0cmc3In0.q6DGhvJla097_6b-RMi0Fw' });

const Mapbox = props => {

let geojson
let locations = []

fetch('http://localhost:4000/api/v1/listings')
.then(r => r.json())
.then(parsed => {
  geojson = parsed
  console.log(geojson)

  geojson.map(listing => {
    geocodingClient
    .forwardGeocode({
      query: `${listing.location}`,
      limit: 1
    })
    .send()
    .then(response => {
      const match = response.body.features[0].center;
      locations.push(match)
      console.log(locations)
    });
  })
})

const makeFeatures = () => {
  console.log("locations", locations)
  locations.each(coordinate => {
    return (
      <Feature coordinates={coordinate}/>
    )
  })
}

return (
<div>
  <Map
  style="mapbox://styles/mapbox/streets-v9"
  center={[-74.0060, 40.7128]}
  containerStyle={{
    height: "100vh",
    width: "100vw"
  }}>
    <Layer
    type="symbol"
    id="marker"
    layout={{ "icon-image": "marker-15" }}>
    {makeFeatures()}
    </Layer>

    <Layer>
    <GeoJSONLayer
    data={geojson}
    symbolLayout={{
      "text-field": "{place}",
      "text-font": ["Open Sans Semibold", "Arial Unicode MS Bold"],
      "text-offset": [0, 0.6],
      "text-anchor": "top"
    }}/>
    </Layer>
  </Map>


  </div>
)


}

export default Mapbox
