import React, { Component } from 'react'
import ReactMapboxGl, { Layer, Feature, Popup } from "react-mapbox-gl";
import { GeoJSONLayer } from "react-mapbox-gl";

const Map = ReactMapboxGl({
  accessToken: process.env.REACT_APP_KEY
})

const mbxGeocoding = require('@mapbox/mapbox-sdk/services/geocoding');
const geocodingClient = mbxGeocoding({ accessToken: process.env.REACT_APP_KEY });

class Mapbox extends React.Component {

state = {
  locations: [],
  geojson: null,
  currentListing: null
}

componentDidMount() {
  fetch('http://localhost:4000/api/v1/listings')
  .then(r => r.json())
  .then(parsed => {
    this.setState({geojson: parsed})
    console.log(this.state.geojson)

    this.state.geojson.map(listing => {
        let locations = this.state.locations
        let coordinates = listing.coordinates.split(",").map(n => parseFloat(n))
        this.setState({locations: [...locations, coordinates]})
    })
    console.log("locations", this.state.locations)
  })
}

makeFeatures = () => {
  console.log("locations", this.state.locations)
  let pins = this.state.locations.map(coordinate =>
      <Feature coordinates={coordinate} onClick={(coordinate) => this.clickHandler()}/>)
  console.log("pins",pins)
  return(pins)
}

clickHandler = (coordinate) => {
  console.log("clicking")
  console.log(coordinate)

  return (
    <Popup latitude={coordinate.lngLat.lat} longitude={coordinate.lngLat.lng}>
      <div>
        hello
      </div>
    </Popup>
  )
}

render() {
  return (
    <div>
      <Map
      style="mapbox://styles/mapbox/streets-v9"
      center={[-74.0060, 40.7128]}
      containerStyle={{
        height: "100vh",
        width: "100vw"
      }}>

        <Layer
        type="symbol"
        id="marker"
        layout={{ "icon-image": "rocket-15" }}>

          {this.makeFeatures()}

        </Layer>

      </Map>


    </div>
  )
}


}

export default Mapbox
